import React from 'react';


class App extends React.Component {
  constructor(props) {
    super();
    this.state = {
      displayText: "hej",
      recipeName: "",
      recipeInstr: "",
    };
    this.getData = this.getData.bind(this);
}
  render() {
    return <div class="container-fluid padding_dd">
            <div class="carousel-caption">
              <div class="row">
                <div class="col-xl-6 col-lg-6 col-md-6 col-sm-12">
                  <div class="text-bg">
                  <span>Welcome to the recipe suggester!</span>
                    <h1>{this.state.recipeName}</h1>
                    <p>{this.state.recipeInstr}</p>
                    <form class="Vegetable">
              <button onClick={this.getData}>Fetch recipe</button>
            </form>
                    <a href="#">Contact Us</a> <a href="#">Vegetable</a>
                  </div>
                </div>
                <div class="col-xl-6 col-lg-6 col-md-6 col-sm-12">
                  <div class="images_box">
                    <figure></figure>
                  </div>
                </div>
              </div>
            </div>
            </div>
  }

  getData() {
    fetch("http://127.0.0.1:5000/getMealData")
    .then(response => 
    response.json()
    .then(json => {
      console.log(json.meals[0])
      this.setState({recipeName: json.meals[0].strMeal, recipeInstr: json.meals[0].strInstructions})
    }))
    .catch(err => {
      throw new Error(err)
    })
    
  }
}


export default App;

<div>

            <button onClick={this.getData}> Click to be suggested a recipe! </button>
            <div>{this.state.recipeName}</div>
            <div>{this.state.recipeInstr}</div>
            </div>
